const getFormKey = (label: string, type: string) =>
  `${label}__${type}`;

const allForms = sections
  .filter(section => section.title !== "Frequently used")
  .flatMap(section => section.items);

  const getUsageMap = (): Record<string, number> => {
  try {
    const raw = localStorage.getItem("formUsage");
    return raw ? JSON.parse(raw) : {};
  } catch {
    return {};
  }
};

const getFrequentlyUsedItems = () => {
  const usage = getUsageMap();

  return allForms
    .map(item => ({
      ...item,
      count: usage[getFormKey(item.label, item.type)] || 0
    }))
    .filter(item => item.count > 0)
    .sort((a, b) => b.count - a.count)
    .slice(0, 5);
};
const frequentlyUsedItems = getFrequentlyUsedItems();

const dynamicSections = [
  {
    title: "Frequently used",
    items:
      frequentlyUsedItems.length > 0
        ? frequentlyUsedItems
        : sections.find(s => s.title === "Frequently used")?.items || []
  },
  ...sections.filter(section => section.title !== "Frequently used")
];
const incrementFormUsage = (label: string, type: string) => {
  const key = `${label}__${type}`;

  const raw = localStorage.getItem("formUsage");
  const usage = raw ? JSON.parse(raw) : {};

  usage[key] = (usage[key] || 0) + 1;

  localStorage.setItem("formUsage", JSON.stringify(usage));
};


